mapper.update:
update cases
cause = #{cause}, agreement = #{agreement}, request = #{request}, caseOfficer = #{caseOfficer}, responsiblePersonOpinion = #{responsiblePersonOpinion}, departmentOpinion = #{departmentOpinion}, secretaryGeneralOpinion = #{secretaryGeneralOpinion}, money = #{money}, acceptCharges = #{acceptCharges}, handlingCharges = #{handlingCharges}, arbitrationCharges = #{arbitrationCharges}, paidCharges = #{paidCharges}, debtCharges = #{debtCharges}, isRelief = #{isRelief}, date = #{date}, registerDate = #{registerDate}, formationDate = #{formationDate}, courtDate = #{courtDate}, concludedDate = #{concludedDate}, applicableProcedure = #{applicableProcedure}, type = #{type}, typeName = #{typeName}, awardResult = #{awardResult}, arbitratorNames = #{arbitratorNames},
uid = #{user.id},
status = #{status}, hearingRecord = #{hearingRecord}
where id = #{id}

mapper.select:
select 
where status = #{status}
order by date desc

select 
where c.status = #{status}
and c.uid = #{uid}
order by date

select top 20 
where c.uid = #{uid}
and c.status > #{status}
order by date desc

select count(*) from cases
where date like '${date}'
and uid = ${uid}

select top ${numPerPage} 
where c.uid = ${uid} and c.status >= ${startStatus}
and c.id not in (select top ((${pageNum}-1)*${numPerPage}) id from cases
	where c.uid = ${uid} and c.status >= ${startStatus}

		order by registerDate desc
		order by formationDate desc
		order by formationDate desc


		order by ${orderField} ${orderDirection}
	)

		order by registerDate desc
		order by formationDate desc
		order by formationDate desc


	order by c.${orderField} ${orderDirection}
