mapper.update:
update receipt
cause = #{cause}, addressee = #{addressee}, address = #{address}, mode = #{mode}, files = #{files}, contents = #{contents}, piece = #{piece}, deliveredDate = #{deliveredDate}, reason = #{reason}, secretary = #{secretary}, processServer = #{processServer}, description = #{description}, receiver = #{receiver}, receiptDate = #{receiptDate}, isReceive = #{isReceive}, status = #{status}
where id = #{id}
